function predictImage() {
  // console.log('Processing...');  
  
  let image = cv.imread(canvas);
  // update image in grayscale
  cv.cvtColor(image, image, cv.COLOR_RGBA2GRAY, 0);        
  cv.threshold(image, image, 175, 255, cv.THRESH_BINARY);  // 175 for all 3 channels (gray) => all above 255 white, also gives sharp contours

  // find contours/boundaries of drawing
  let contours = new cv.MatVector();  
  let hierarchy = new cv.Mat();
  cv.findContours(image, contours, hierarchy, cv.RETR_CCOMP, cv.CHAIN_APPROX_SIMPLE);

  // bounding image using contours 
  let cnt = contours.get(0);  
  let rect = cv.boundingRect(cnt);
  image = image.roi(rect);  // update image
  
  // resize image (high and wide imas differently)
  var height = image.rows;  
  var width = image.cols;

  if (height > width) {
      height = 20;
      const scaleFactor = image.rows / height;
      width = Math.round(image.cols/scaleFactor);
  } else {
      width = 20;
      const scaleFactor = image.cols / width;
      height = Math.round(image.rows/scaleFactor);
  }   
  
  let newSize = new cv.Size(width, height);
  cv.resize(image, image, newSize, 0, 0, cv.INTER_AREA);  // resize finish

  // Padding, round up half pix
  const LEFT = Math.ceil(4 + (20 - width)/2);  
  const RIGHT = Math.floor(4 + (20 - width)/2);   // round down
  const TOP = Math.ceil(4 + (20 - height)/2); 
  const BOTTOM = Math.floor(4 + (20 - height)/2); 
  // console.log(`top: ${TOP}, bottom: ${BOTTOM}, left ${LEFT}, right: ${RIGHT}`);
  
  const BLACK = new cv.Scalar(0, 0, 0, 0); 
  cv.copyMakeBorder(image, image, TOP, BOTTOM, LEFT, RIGHT, cv.BORDER_CONSTANT, BLACK); // Add black padding actually
  
  // Centre of Mass
  cv.findContours(image, contours, hierarchy, cv.RETR_CCOMP, cv.CHAIN_APPROX_SIMPLE);
  cnt = contours.get(0);  // this is another updated ima, not the one above (already used this smippet)
  const Moments = cv.moments(cnt, false);   // false (not a binary image)
  
  const cx = Moments.m10 / Moments.m00    // m10: mass in x direction, m00: whole shape area/mass
  const cy = Moments.m01 / Moments.m00
  // console.log(`M00: ${Moments.m00}, cx: ${cx}, cy: ${cy}`);  // largest M00 we can have is 20x20. cx, cy for centre: 14, 14 (0, 0 is the top left corner)   
  
  // Shift image based on CoM
  const X_SHIFT = Math.round(image.cols/2.0 - cx);
  const Y_SHIFT = Math.round(image.rows/2.0 - cy);  
  
  newSize = new cv.Size(image.cols, image.rows);  // give it a new value, the current size of image (width, height) 
  const M = cv.matFromArray(2, 3, cv.CV_64FC1, [1, 0, X_SHIFT, 0, 1, Y_SHIFT]);
  cv.warpAffine(image, image, M, newSize, cv.INTER_LINEAR, cv.BORDER_CONSTANT, BLACK); 
  
  // Normalize pix values (/255)
  let pixelValues = image.data;
  // console.log(`Pixel values: ${pixelValues}`)
  
  pixelValues = Float32Array.from(pixelValues);  // Int to Float

  // map method will use a function as arg and devide pix vals by 255
  pixelValues = pixelValues.map(function(item){
      return item / 255.0;
  });
  
  // console.log(`Scaled array: ${pixelValues}`);  // no decimal vals, only 0, 1. ABove, "image.data" gives Integers
  
  // Create tensor from image
  const X = tf.tensor([pixelValues])  // 2d (that's why 2 brackets
  // console.log(`Shape of Tensor: ${X.shape}`);  // 1784 OK!
  // console.log(`dtype of Tensor: ${X.dtype}`);
  
  // Make prediction based on Canvas data. Reuse predictImage above, but need to change scope of "model" and put out as global
  const result = model.predict(X);
  result.print();  // WORKIIIIIIIIIIIIIIIING!!!
  
  // Get out tensor value for checking against answer in maths.js
  const output = result.dataSync()[0];  // We can fetch the 1st elem, as we only have one prediction
    
    
  // console.log(tf.memory());  
  // we're using more and more memory every time we predict. 
  // ".dispose" of tensors, comment out above tensors. 11 tens, X bytes won't increase. No more bleeding out mem. 
  
  
  // TESTING ONLY: We'll also display the read-in canvas to verify
  // const outputCanvas = document.createElement('CANVAS');
  // cv.imshow(outputCanvas, image);
  // append feedback canvas to the body of the web document
  // document.body.appendChild(outputCanvas);
 
 
  // Cleanup
  image.delete();
  contours.delete();
  cnt.delete();
  hierarchy.delete();
  M.delete();
  X.dispose();
  result.dispose();


  return output;
   
}


